import { createSlice, createAsyncThunk, nanoid } from "@reduxjs/toolkit";
import axios from "axios";

export const getNotesAsync = createAsyncThunk(
  "notes/getNotesAsync",
  async () => {
    const res = await axios.get(`${process.env.REACT_APP_BASE_ENDPOINT}/notes`);

    return res.data;
  }
);

export const addNoteAsync = createAsyncThunk(
  "notes/addNoteAsync",
  async (item) => {
    const res = await axios.post(
      `${process.env.REACT_APP_BASE_ENDPOINT}/notes`,
      item
    );

    console.log("post response", res);

    return res;
  }
);

export const notesSlice = createSlice({
  name: "notes",
  initialState: {
    items: [],
    selectedItem: {
      Title: "",
      Body: "",
      Color: "f94144",
    },
  },
  reducers: {
    // functions here:
  },
  extraReducers: {
    [getNotesAsync.fulfilled]: (state, action) => {
      state.items = action.payload;
    },
    [addNoteAsync.fulfilled]: (state, action) => {
      state.items.push(action.payload);
      console.log(action.payload);
    },
  },
});

export default notesSlice.reducer;
