import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const getNotesAsync = createAsyncThunk(
  "notes/getNotesAsync",
  async () => {
    const res = await axios.get(`${process.env.REACT_APP_BASE_ENDPOINT}/notes`);

    return res.data;
  }
);

export const addNotesAsync = createAsyncThunk(
  "notes/addNotesAsync",
  async (item) => {
    const res = await axios.post(
      `${process.env.REACT_APP_BASE_ENDPOINT}/notes`,
      item
    );

    console.log("post response", res);

    return res;
  }
);

export const notesSlice = createSlice({
  name: "notes",
  initialState: {
    items: [],
    selectedItem: {
      id: "",
      title: "",
      body: "",
      color: "f94144",
    },
  },
  reducers: {
    selectNote: (state, action) => {
      state.selectedItem = state.items.find((x) => x.id === action.payload.id);
    },
  },
  extraReducers: {
    [getNotesAsync.fulfilled]: (state, action) => {
      state.items = action.payload;
    },
    [addNotesAsync.fulfilled]: (state, action) => {
      state.items.push(action.payload);
    },
  },
});

export const { selectNote } = notesSlice.actions;

export default notesSlice.reducer;
