import {
  FormControl,
  FormLabel,
  Input,
  Textarea,
  Button,
  Box,
  Flex,
} from "@chakra-ui/react";
import { CheckIcon } from "@chakra-ui/icons";

function Form() {
  return (
    <form>
      <FormControl>
        <Input placeholder="Title" w={"300px"} />
        <Textarea placeholder="Write your notes here..." mt={2}></Textarea>
        <Flex justifyContent={"space-between"} p={2}>
          <Box>
            <div className="color" style={{ backgroundColor: "#f94144" }}>
              <CheckIcon color={"white"} />
            </div>
          </Box>
          <Button type="submit" colorScheme="green">
            Save
          </Button>
        </Flex>
      </FormControl>
    </form>
  );
}

export default Form;
