import {
  FormControl,
  Input,
  Textarea,
  Button,
  HStack,
  Flex,
} from "@chakra-ui/react";
import { CheckIcon } from "@chakra-ui/icons";
import { useSelector } from "react-redux";
import { useState } from "react";

function Form() {
  const selectedItem = useSelector((state) => state.notes.selectedItem);
  const { selectedColor, setSelectedColor } = useState("f94144");

  return (
    <form>
      <FormControl>
        <Input placeholder="Title" w={"300px"} />
        <Textarea placeholder="Write your notes here..." mt={2}></Textarea>
        <Flex justifyContent={"space-between"} p={2}>
          <HStack>
            <div className="color" style={{ backgroundColor: "#f94144" }}>
              {selectedColor == "f94144" && <CheckIcon color={"white"} />}
            </div>
            <div className="color" style={{ backgroundColor: "#f9844a" }}>
              {selectedColor == "f9844a" && <CheckIcon color={"white"} />}
            </div>
            <div className="color" style={{ backgroundColor: "#f9c74f" }}>
              {selectedColor == "f9c74f" && <CheckIcon color={"white"} />}
            </div>
            <div className="color" style={{ backgroundColor: "#43aa8b" }}>
              {selectedColor == "43aa8b" && <CheckIcon color={"white"} />}
            </div>
            <div className="color" style={{ backgroundColor: "#4d908e" }}>
              {selectedColor == "4d908e" && <CheckIcon color={"white"} />}
            </div>
          </HStack>
          <Button type="submit" colorScheme="green">
            Save
          </Button>
        </Flex>
      </FormControl>
    </form>
  );
}

export default Form;
