import {
  FormControl,
  FormLabel,
  Input,
  Textarea,
  Button,
  Box,
  Flex,
} from "@chakra-ui/react";

function Form() {
  return (
    <form>
      <FormControl>
        <Input placeholder="Title" />
        <Textarea placeholder="Write your notes here..."></Textarea>
        <Flex justifyContent={"space-between"} p={2}>
          <Box></Box>
          <Button type="submit" colorScheme="green">
            Save
          </Button>
        </Flex>
      </FormControl>
    </form>
  );
}

export default Form;
